$where = array(
    \'msDelivery.active\' => true,
    \'msPayment.active\' => true,
);

// Join payments to deliveries
$leftJoin = array(
    \'Payments\' => array(
        \'class\' => \'msDeliveryMember\',
    ),
    \'msPayment\' => array(
        \'class\' => \'msPayment\',
        \'on\' => \'Payments.payment_id = msPayment.id\',
    ),
);

// Select columns
