    $delivery = array();
    $payment = array();
    foreach ($row as $key => $value) {
        if (strpos($key, \'delivery_\') === 0) {
            $delivery[substr($key, 9)] = $value;
        } else {
            $payment[substr($key, 8)] = $value;
