(34, 1, 0, 'tpl.msEmail.cancelled.user', '', 0, 18, 0, '[[!msGetOrder?id=`[[+id]]`]]\n\n<h3>[[%ms2_email_subject_cancelled_user]]</h3>\n\n<div id=\"msCart\">\n	<table class=\"table table-striped\">\n		<tr class=\"header\">\n			<th class=\"image span2 col-md-2\">&nbsp;</th>\n			<th class=\"title span4 col-md-4\">[[%ms2_cart_title]]</th>\n			<th class=\"count span2 col-md-2\">[[%ms2_cart_count]]</th>\n			<th class=\"weight span1 col-md-1\">[[%ms2_cart_weight]]</th>\n			<th class=\"price span1 col-md-1\">[[%ms2_cart_cost]]</th>\n		</tr>\n		[[+goods]]\n		<tr class=\"footer\">\n			<th class=\"total\" colspan=\"2\">[[%ms2_cart_total]]:</th>\n			<th class=\"total_count\"><span class=\"ms2_total_count\">[[+cart_count]]</span> [[%ms2_frontend_count_unit]]</th>\n			<th class=\"total_weight\"><span class=\"ms2_total_weight\">[[+cart_weight]]</span> [[%ms2_frontend_weight_unit]]</th>\n			<th class=\"total_cost\"><span class=\"ms2_total_cost\">[[+cart_cost]]</span> [[%ms2_frontend_currency]]</th>\n		</tr>\n	</table>\n	<h4>[[%ms2_frontend_order_cost]]: [[+cart_cost]] [[%ms2_frontend_currency]] + [[+delivery_cost]] [[%ms2_frontend_currency]] = <big>[[+cost]]</big> [[%ms2_frontend_currency]]</h4>\n</div>', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_email_cancelled_user.tpl'),
(35, 1, 0, 'tpl.msOrder.outer', '', 0, 18, 0, '<form class=\"form-horizontal ms2_form\" id=\"msOrder\" method=\"post\">\n	<div class=\"row\">\n		<div class=\"span6 col-md-6\">\n			<h4>[[%ms2_frontend_credentials]]:</h4>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\" for=\"email\"><span class=\"required-star\">*</span> [[%ms2_frontend_email]]</label>\n				<div class=\"col-sm-6\">\n					<input type=\"email\" id=\"email\" placeholder=\"[[%ms2_frontend_email]]\" name=\"email\" value=\"[[+email]]\" class=\"form-control [[+errors.email]]\">\n				</div>\n			</div>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\"  for=\"receiver\"><span class=\"required-star\">*</span> [[%ms2_frontend_receiver]]</label>\n				<div class=\"col-sm-6\">\n					<input type=\"text\" id=\"receiver\" placeholder=\"[[%ms2_frontend_receiver]]\" name=\"receiver\" value=\"[[+receiver]]\" class=\"form-control [[+errors.receiver]]\">\n				</div>\n			</div>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\"  for=\"phone\"><span class=\"required-star\">*</span> [[%ms2_frontend_phone]]</label>\n				<div class=\"col-sm-6\">\n					<input type=\"text\" id=\"phone\" placeholder=\"[[%ms2_frontend_phone]]\" name=\"phone\" value=\"[[+phone]]\" class=\"form-control [[+errors.phone]]\">\n				</div>\n			</div>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\"  for=\"comment\"><span class=\"required-star\">*</span> [[%ms2_frontend_comment]]</label>\n				<div class=\"col-sm-6\">\n					<textarea name=\"comment\" id=\"comment\" placeholder=\"[[%ms2_frontend_comment]]\" class=\"form-control [[+errors.comment]]\">[[+comment]]</textarea>\n				</div>\n			</div>\n		</div>\n		<div class=\"span6 col-md-6\" id=\"payments\">\n			<h4>[[%ms2_frontend_payments]]:</h4>\n			<div class=\"form-group\">\n				<label class=\"col-sm-4 control-label\" ><span class=\"required-star\">*</span> [[%ms2_frontend_payment_select]]</label>\n				<div class=\"col-sm-6\">\n					[[+payments]]\n				</div>\n			</div>\n		</div>\n	</div>\n	<div class=\"row\">\n		<div class=\"span6 col-md-6\" id=\"deliveries\">\n			<h4>[[%ms2_frontend_deliveries]]:</h4>\n			<div class=\"form-group\">\n				<label class=\"col-sm-4 control-label\" ><span class=\"required-star\">*</span> [[%ms2_frontend_delivery_select]]</label>\n				<div class=\"col-sm-6\">\n					[[+deliveries]]\n				</div>\n			</div>\n		</div>\n		<div class=\"span6 col-md-6\">\n			<h4>[[%ms2_frontend_address]]:</h4>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\" for=\"index\"><span class=\"required-star\">*</span> [[%ms2_frontend_index]]</label>\n				<div class=\"col-sm-4\">\n					<input type=\"text\" id=\"index\" placeholder=\"[[%ms2_frontend_index]]\"  name=\"index\" value=\"[[+index]]\" class=\"form-control [[+errors.index]]\">\n				</div>\n			</div>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\"  for=\"region\"><span class=\"required-star\">*</span> [[%ms2_frontend_region]]</label>\n				<div class=\"col-sm-6\">\n					<input type=\"text\" id=\"region\" placeholder=\"[[%ms2_frontend_region]]\" name=\"region\" value=\"[[+region]]\" class=\"form-control [[+errors.region]]\">\n				</div>\n			</div>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\"  for=\"city\"><span class=\"required-star\">*</span> [[%ms2_frontend_city]]</label>\n				<div class=\"col-sm-6\">\n					<input type=\"text\" id=\"city\" placeholder=\"[[%ms2_frontend_city]]\" name=\"city\" value=\"[[+city]]\" class=\"form-control [[+errors.city]]\">\n				</div>\n			</div>\n			<div class=\"form-group input-parent\">\n				<label class=\"col-sm-4 control-label\"  for=\"street\"><span class=\"required-star\">*</span> [[%ms2_frontend_street]]</label>\n				<div class=\"col-md-6\">\n					<div class=\"col-md-4\">\n						<input type=\"text\" id=\"street\" placeholder=\"[[%ms2_frontend_street]]\" name=\"street\" value=\"[[+street]]\" class=\"form-control [[+errors.street]]\">\n					</div>\n					<div class=\"col-md-4\">\n						<input type=\"text\" id=\"building\" placeholder=\"[[%ms2_frontend_building]]\" name=\"building\" value=\"[[+building]]\" class=\"form-control [[+errors.building]]\">\n					</div>\n					<div class=\"col-md-4\">\n						<input type=\"text\" id=\"room\" placeholder=\"[[%ms2_frontend_room]]\" name=\"room\" value=\"[[+room]]\" class=\"form-control [[+errors.room]]\">\n					</div>\n				</div>\n			</div>\n		</div>\n	</div>\n	<button type=\"button\" name=\"ms2_action\" value=\"order/clean\" class=\"btn btn-default ms2_link\"><i class=\"glyphicon glyphicon-remove\"></i> [[%ms2_frontend_order_cancel]]</button>\n	<hr/>\n	<div class=\"well\">\n		<div class=\"col-md-offset-2\">\n			<h3>[[%ms2_frontend_order_cost]]: <span id=\"ms2_order_cost\">[[+order_cost:default=`0`]]</span> [[%ms2_frontend_currency]]</h3>\n			<button type=\"submit\" name=\"ms2_action\" value=\"order/submit\" class=\"btn btn-default btn-primary ms2_link\">[[%ms2_frontend_order_submit]]</button>\n		</div>\n	</div>\n</form>', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_order_outer.tpl'),
(36, 1, 0, 'tpl.msOrder.payment', '', 0, 18, 0, '<div class=\"checkbox\">\n	<label class=\"payment input-parent\">\n		<input type=\"radio\" name=\"payment\" value=\"[[+id]]\" id=\"payment_[[+id]]\" [[+checked]]>\n		[[+logo]]\n		[[+description]]\n	</label>\n</div>\n<!--minishop2_logo <img src=\"[[+logo]]\" />-->\n<!--minishop2_!logo [[+name]]-->\n<!--minishop2_description <p><small>[[+description]]</small></p>-->\n', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_order_payment.tpl'),
(37, 1, 0, 'tpl.msOrder.delivery', '', 0, 18, 0, '<div class=\"checkbox\">\n	<label class=\"delivery input-parent\">\n		<input type=\"radio\" name=\"delivery\" value=\"[[+id]]\" id=\"delivery_[[+id]]\" data-payments=\'[[+payments]]\' [[+checked]]>\n		[[+logo:default=`[[+name]]`]] <!--&mdash; <i>[[+price]] [[%ms2_frontend_currency]]</i>-->\n		[[+description]]\n	</label>\n</div>\n<!--minishop2_logo <img src=\"[[+logo]]\" />-->\n<!--minishop2_description <p><small>[[+description]]</small></p>-->', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_order_delivery.tpl'),
(38, 1, 0, 'tpl.msOrder.success', '', 0, 18, 0, '[[!msGetOrder?id=`[[+id]]`]]\n\n<h3>[[%ms2_frontend_order_success]]</h3>\n\n<div id=\"msCart\">\n	<table class=\"table table-striped\">\n		<tr class=\"header\">\n			<th class=\"image span2 col-md-2\">&nbsp;</th>\n			<th class=\"title span4 col-md-4\">[[%ms2_cart_title]]</th>\n			<th class=\"count span2 col-md-2\">[[%ms2_cart_count]]</th>\n			<th class=\"weight span1 col-md-1\">[[%ms2_cart_weight]]</th>\n			<th class=\"price span1 col-md-1\">[[%ms2_cart_cost]]</th>\n		</tr>\n		[[+goods]]\n		<tr class=\"footer\">\n			<th class=\"total\" colspan=\"2\">[[%ms2_cart_total]]:</th>\n			<th class=\"total_count\"><span class=\"ms2_total_count\">[[+cart_count]]</span> [[%ms2_frontend_count_unit]]</th>\n			<th class=\"total_weight\"><span class=\"ms2_total_weight\">[[+cart_weight]]</span> [[%ms2_frontend_weight_unit]]</th>\n			<th class=\"total_cost\"><span class=\"ms2_total_cost\">[[+cart_cost]]</span> [[%ms2_frontend_currency]]</th>\n		</tr>\n	</table>\n	<h4>[[%ms2_frontend_order_cost]]: [[+cart_cost]] [[%ms2_frontend_currency]] + [[+delivery_cost]] [[%ms2_frontend_currency]] = <big>[[+cost]]</big> [[%ms2_frontend_currency]]</h4>\n</div>', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_order_success.tpl'),
(39, 1, 0, 'tpl.msGetOrder.row', '', 0, 18, 0, '<tr>\n	<td class=\"image\"><img src=\"[[++site_url]][[+thumb:default=`[[++assets_url]]components/minishop2/img/web/ms2_small.png`]]\" /></td>\n	<td class=\"title\">[[+link:isnot=``:then=`<a href=\"[[+link]]\">[[+name]]</a>`:else=`[[+name]]`]]<br/>\n		<small><i>[[+option.color]] [[+option.size]]</i></small>\n	</td>\n	<td class=\"count\">[[+count]] [[%ms2_frontend_count_unit]]</td>\n	<td class=\"weight\">[[+weight]] [[%ms2_frontend_weight_unit]]</td>\n	<td class=\"price\">[[+price]] [[%ms2_frontend_currency]]</td>\n</tr>\n<!--minishop2_option.color [[%ms2_frontend_color]]: [[+option.color]];-->\n<!--minishop2_option.size [[%ms2_frontend_size]]: [[+option.size]];-->\n', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_get_order_row.tpl'),
(40, 1, 0, 'tpl.msProductOptions.outer', '', 0, 18, 0, '<table>\n    [[+rows]]\n</table>', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_product_options_outer.tpl'),
(41, 1, 0, 'tpl.msProductOptions.row', '', 0, 18, 0, '<tr>\n    <td>\n        <b>[[+caption]]:</b>\n    </td>\n    <td>\n        [[+value]]\n    </td>\n</tr>', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_product_options_row.tpl'),
(85, 0, 0, 'modalTpl', '', 0, 10, 0, '<div id=\"animatedModal\" class=\"d-flex\">\n    <!--THIS IS IMPORTANT! to close the modal, the class name has to match the name given on the ID  class=\"close-animatedModal\" -->\n    <div class=\"close-animatedModal\"> \n        CLOSE MODAL\n    </div>\n        \n    <div class=\"modal-content container p-4\">\n       \n        <form>\n            <div class=\"form-group row\">\n                <label class=\"col-sm-2 col-form-label\">Вы выбрали ежонка </label>\n                <div class=\"col-sm-10\">\n                    <input type=\"text\" class=\"form-control-plaintext\" readonly value=\"test\">\n                </div>\n            </div>\n            <div class=\"form-group row\">\n                <label class=\"col-sm-2 col-form-label\">Укажите ваш email</label>\n                <div class=\"col-sm-10\">\n                    <input type=\"email\" class=\"form-control\" id=\"exampleInputEmail1\" aria-describedby=\"emailHelp\" placeholder=\"Enter email\" required>\n                    <small class=\"form-text text-muted p-1\">Мы никогда не будем делиться вашей электронной почтой с кем-либо еще.</small>\n                </div>\n            </div>\n            <div class=\"form-group\">\n                <label for=\"message\">Текст заявки</label>\n                <textarea class=\"form-control\" id=\"message\" placeholder=\"Наберите текст вашей заявки тут\" rows=\"20\"></textarea>\n            </div>\n            <button type=\"submit\" class=\"btn btn-primary btn-lg btn-block\">Отправить заявку</button>\n        </form>\n    \n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(43, 1, 0, 'galleryTpl', '', 0, 10, 0, '<div class=\"msGallery\">\n  {if $files?}\n    <div class=\"fotorama\"\n        data-nav=\"thumbs\"\n        data-thumbheight=\"45\"\n        data-allowfullscreen=\"true\"\n        data-swipe=\"true\"\n        data-autoplay=\"5000\">\n        {foreach $files as $file}\n            <a href=\"{$file[\'url\']}\" target=\"_blank\">\n              <img src=\"{$file[\'120x90\']}\" alt=\"\" title=\"\">\n            </a>\n        {/foreach}\n    </div>\n  {else}\n    <img src=\"{(\'assets_url\' | option) ~ \'components/minishop2/img/web/ms2_medium.png\'}\"\n         srcset=\"{(\'assets_url\' | option) ~ \'components/minishop2/img/web/ms2_medium@2x.png\'} 2x\"\n         alt=\"\" title=\"\"/>\n  {/if}\n</div>\n', 0, 'a:0:{}', 1, 'assets/static/chunks/galleryTpl.chunks.tpl'),
(83, 1, 0, 'parentsTpl', '', 0, 10, 0, '<div class=\"row ms2_product\" id=\"[[ +alias ]]\">\n\n  <div class=\"col-md-4\">\n    \n    <div class=\"row\">\n      <div class=\"col-md-12\">\n        [[!msOptions?\n          &product=`[[ +id ]]`\n          &options=`sex, color, birthdate`\n          &tpl=`parentProductOptionsTpl`\n        ]]\n      </div>\n    </div>\n    \n  </div>\n\n  <div class=\"col-md-8\">\n    \n    <div class=\"row\">\n      <div class=\"col-md-12\">\n        [[ !msGallery? \n          &product=`[[ +id ]]`\n          &offset=`0`\n          &limit=`10`\n          &tpl=`galleryTpl`\n        ]]\n      </div>\n      \n    </div>\n  </div>\n\n\n</div>\n', 0, 'a:0:{}', 0, '.gitignore'),
(79, 1, 0, 'productOptionsTpl', '', 0, 10, 0, '<h2 class=\"text-center\">\n  {* <a href=\"[[~[[+id]]]]\"> *}\n    [[ +pagetitle ]]\n  {* </a> *}\n</h2>\n<div class=\"col-md-12\">\n  <div>Пол:</div>\n  <h4 class=\"text-center\">{$options.sex.0}</h4>\n</div>\n<div class=\"col-md-12\">\n  <div>Окрас: </div>\n  <h4 class=\"text-center\">{$options[\'color\'][0]}</h4>\n</div>\n<div class=\"col-md-12\">\n  <div>Дата рождения:</div>\n  <div>\n    <h4 class=\"text-center\">{$dt = $.php.date_create_from_format(\'Y-m-d\', $options.birthdate.0)} {$.php.date_format($dt, \'d-m-Y\')}</h4>\n  </div>\n</div>\n<div class=\"col-md-12\">\n  <div>Родители</div>\n  <div class=\"row\">\n    [[ !msProducts?\n      &parents=`0`\n      &link=`1`\n      &slave=`[[ +id ]]`\n      &tpl=`parentOptionsTpl`\n    ]]\n  </div>\n</div>\n', 0, 'a:0:{}', 1, 'assets/static/chunks/productOptionsTpl.chunks.tpl'),
(44, 1, 0, 'tpl.msCart', '', 0, 15, 0, '<div id=\"msCart\">\n    {if !count($products)}\n        {\'ms2_cart_is_empty\' | lexicon}\n    {else}\n        <div class=\"table-responsive\">\n            <table class=\"table table-striped\">\n                <tr class=\"header\">\n                    <th class=\"image\">&nbsp;</th>\n                    <th class=\"title\">{\'ms2_cart_title\' | lexicon}</th>\n                    <th class=\"count\">{\'ms2_cart_count\' | lexicon}</th>\n                    <th class=\"weight\">{\'ms2_cart_weight\' | lexicon}</th>\n                    <th class=\"price\">{\'ms2_cart_price\' | lexicon}</th>\n                    <th class=\"remove\">{\'ms2_cart_remove\' | lexicon}</th>\n                </tr>\n\n                {foreach $products as $product}\n                    <tr id=\"{$product.key}\">\n                        <td class=\"image\">\n                            {if $product.thumb?}\n                                <img src=\"{$product.thumb}\" alt=\"{$product.pagetitle}\" title=\"{$product.pagetitle}\"/>\n                            {else}\n                                <img src=\"{\'assets_url\' | option}components/minishop2/img/web/ms2_small.png\"\n                                     srcset=\"{\'assets_url\' | option}components/minishop2/img/web/ms2_small@2x.png 2x\"\n                                     alt=\"{$product.pagetitle}\" title=\"{$product.pagetitle}\"/>\n                            {/if}\n                        </td>\n                        <td class=\"title\">\n                            {if $product.id?}\n                                <a href=\"{$product.id | url}\">{$product.pagetitle}</a>\n                            {else}\n                                {$product.name}\n                            {/if}\n                            {if $product.options?}\n                                <div class=\"small\">\n                                    {$product.options | join : \'; \'}\n                                </div>\n                            {/if}\n                        </td>\n                        <td class=\"count\">\n                            <form method=\"post\" class=\"ms2_form form-inline\" role=\"form\">\n                                <input type=\"hidden\" name=\"key\" value=\"{$product.key}\"/>\n                                <div class=\"form-group\">\n                                    <input type=\"number\" name=\"count\" value=\"{$product.count}\"\n                                           class=\"input-sm form-control\"/>\n                                    <span class=\"hidden-xs\">{\'ms2_frontend_count_unit\' | lexicon}</span>\n                                    <button class=\"btn btn-default\" type=\"submit\" name=\"ms2_action\" value=\"cart/change\">\n                                        <i class=\"glyphicon glyphicon-refresh\"></i>\n                                    </button>\n                                </div>\n                            </form>\n                        </td>\n                        <td class=\"weight\">\n                            <span>{$product.weight}</span> {\'ms2_frontend_weight_unit\' | lexicon}\n                        </td>\n                        <td class=\"price\">\n                            <span>{$product.price}</span> {\'ms2_frontend_currency\' | lexicon}\n                            {if $product.old_price?}\n                            <span class=\"old_price\">{$product.old_price}</span> {\'ms2_frontend_currency\' | lexicon}\n                                {/if}\n                        </td>\n                        <td class=\"remove\">\n                            <form method=\"post\" class=\"ms2_form\">\n                                <input type=\"hidden\" name=\"key\" value=\"{$product.key}\">\n                                <button class=\"btn btn-default\" type=\"submit\" name=\"ms2_action\" value=\"cart/remove\">\n                                    <i class=\"glyphicon glyphicon-remove\"></i>\n                                </button>\n                            </form>\n                        </td>\n                    </tr>\n                {/foreach}\n\n                <tr class=\"footer\">\n                    <th class=\"total\" colspan=\"2\">{\'ms2_cart_total\' | lexicon}:</th>\n                    <th class=\"total_count\">\n                        <span class=\"ms2_total_count\">{$total.count}</span>\n                        {\'ms2_frontend_count_unit\' | lexicon}\n                    </th>\n                    <th class=\"total_weight\">\n                        <span class=\"ms2_total_weight\">{$total.weight}</span>\n                        {\'ms2_frontend_weight_unit\' | lexicon}\n                    </th>\n                    <th class=\"total_cost\">\n                        <span class=\"ms2_total_cost\">{$total.cost}</span>\n                        {\'ms2_frontend_currency\' | lexicon}\n                    </th>\n                    <th>&nbsp;</th>\n                </tr>\n            </table>\n        </div>\n        <form method=\"post\">\n            <button class=\"btn btn-default\" type=\"submit\" name=\"ms2_action\" value=\"cart/clean\">\n                <i class=\"glyphicon glyphicon-remove\"></i> {\'ms2_cart_clean\' | lexicon}\n            </button>\n        </form>\n    {/if}\n</div>\n', 0, NULL, 0, 'core/components/minishop2/elements/chunks/chunk.ms_cart.tpl'),
(45, 1, 0, 'tpl.msOrder', '', 0, 15, 0, '<form class=\"form-horizontal ms2_form\" id=\"msOrder\" method=\"post\">\n    <div class=\"row\">\n        <div class=\"col-md-6\">\n            <h4>{\'ms2_frontend_credentials\' | lexicon}:</h4>\n            {foreach [\'email\',\'receiver\',\'phone\'] as $field}\n                <div class=\"form-group input-parent\">\n                    <label class=\"col-md-4 control-label\" for=\"{$field}\">\n                        <span class=\"required-star\">*</span> {(\'ms2_frontend_\' ~ $field) | lexicon}\n                    </label>\n                    <div class=\"col-sm-6\">\n                        <input type=\"text\" id=\"{$field}\" placeholder=\"{(\'ms2_frontend_\' ~ $field) | lexicon}\"\n                               name=\"{$field}\" value=\"{$form[$field]}\"\n                               class=\"form-control{($field in list $errors) ? \' error\' : \'\'}\">\n                    </div>\n                </div>\n            {/foreach}\n\n            <div class=\"form-group input-parent\">\n                <label class=\"col-md-4 control-label\" for=\"comment\">\n                    <span class=\"required-star\">*</span> {\'ms2_frontend_comment\' | lexicon}</label>\n                <div class=\"col-sm-6\">\n                    <textarea name=\"comment\" id=\"comment\" placeholder=\"{\'ms2_frontend_comment\' | lexicon}\"\n                              class=\"form-control{(\'comment\' in list $errors) ? \' error\' : \'\'}\">{$form.comment}</textarea>\n                </div>\n            </div>\n        </div>\n\n        <div class=\"col-md-6\" id=\"payments\">\n            <h4>{\'ms2_frontend_payments\' | lexicon}:</h4>\n            <div class=\"form-group\">\n                <label class=\"col-md-4 control-label\"><span class=\"required-star\">*</span>\n                    {\'ms2_frontend_payment_select\' | lexicon}</label>\n                <div class=\"col-sm-6\">\n                    {foreach $payments as $payment index