(62, 1, 0, 'msGetOrder', '', 0, 15, 0, '/** @var modX $modx */\n/** @var array $scriptProperties */\n/** @var miniShop2 $miniShop2 */\n$miniShop2 = $modx->getService(\'miniShop2\');\n$miniShop2->initialize($modx->context->key);\n/** @var pdoFetch $pdoFetch */\nif (!$modx->loadClass(\'pdofetch\', MODX_CORE_PATH . \'components/pdotools/model/pdotools/\', false, true)) {\n    return false;\n}\n$pdoFetch = new pdoFetch($modx, $scriptProperties);\n$pdoFetch->addTime(\'pdoTools loaded.\');\n\n$tpl = $modx->getOption(\'tpl\', $scriptProperties, \'tpl.msGetOrder\');\n\nif (empty($id) && !empty($_GET[\'msorder\'])) {\n    $id = (int)$_GET[\'msorder\'];\n}\nif (empty($id)) {\n    return;\n}\n/** @var msOrder $order */\nif (!$order = $modx->getObject(\'msOrder\', $id)) {\n    return $modx->lexicon(\'ms2_err_order_nf\');\n}\n$canView = (!empty($_SESSION[\'minishop2\'][\'orders\']) && in_array($id, $_SESSION[\'minishop2\'][\'orders\'])) ||\n    $order->get(\'user_id\') == $modx->user->id || $modx->user->hasSessionContext(\'mgr\') || !empty($scriptProperties[\'id\']);\nif (!$canView) {\n    return \'\';\n}\n\n// Select ordered products\n$where = array(\n    \'msOrderProduct.order_id\' => $id,\n);\n\n// Include products properties\n$leftJoin = array(\n    \'msProduct\' => array(\n        \'class\' => \'msProduct\',\n        \'on\' => \'msProduct.id = msOrderProduct.product_id\',\n    ),\n    \'Data\' => array(\n        \'class\' => \'msProductData\',\n        \'on\' => \'msProduct.id = Data.id\',\n    ),\n    \'Vendor\' => array(\n        \'class\' => \'msVendor\',\n        \'on\' => \'Data.vendor = Vendor.id\',\n    ),\n);\n\n// Select columns\n$select = array(\n    \'msProduct\' => !empty($includeContent)\n        ? $modx->getSelectColumns(\'msProduct\', \'msProduct\')\n        : $modx->getSelectColumns(\'msProduct\', \'msProduct\', \'\', array(\'content\'), true),\n    \'Data\' => $modx->getSelectColumns(\'msProductData\', \'Data\', \'\', array(\'id\'),\n            true) . \',`Data`.`price` as `original_price`\',\n    \'Vendor\' => $modx->getSelectColumns(\'msVendor\', \'Vendor\', \'vendor.\', array(\'id\'), true),\n    \'OrderProduct\' => $modx->getSelectColumns(\'msOrderProduct\', \'msOrderProduct\', \'\', array(\'id\'), true),\n);\n\n// Include products thumbnails\nif (!empty($includeThumbs)) {\n    $thumbs = array_map(\'trim\', explode(\',\', $includeThumbs));\n    if (!empty($thumbs[0])) {\n        foreach ($thumbs as $thumb) {\n            $leftJoin[$thumb] = array(\n                \'class\' => \'msProductFile\',\n                \'on\' => \"`{$thumb}`.product_id = msProduct.id AND `{$thumb}`.parent != 0 AND `{$thumb}`.path LIKE \'%/{$thumb}/%\'\",\n            );\n            $select[$thumb] = \"`{$thumb}`.url as \'{$thumb}\'\";\n        }\n        $pdoFetch->addTime(\'Included list of thumbnails: <b>\' . implode(\', \', $thumbs) . \'</b>.\');\n    }\n}\n\n// Add user parameters\nforeach (array(\'where\', \'leftJoin\', \'select\') as $v) {\n    if (!empty($scriptProperties[$v])) {\n        $tmp = $scriptProperties[$v];\n        if (!is_array($tmp)) {\n            $tmp = json_decode($tmp, true);\n        }\n        if (is_array($tmp)) {\n            $$v = array_merge($$v, $tmp);\n        }\n    }\n    unset($scriptProperties[$v]);\n}\n$pdoFetch->addTime(\'Conditions prepared\');\n\n// Tables for joining\n$default = array(\n    \'class\' => \'msOrderProduct\',\n    \'where\' => $where,\n    \'leftJoin\' => $leftJoin,\n    \'select\' => $select,\n    \'joinTVsTo\' => \'msProduct\',\n    \'sortby\' => \'msOrderProduct.id\',\n    \'sortdir\' => \'asc\',\n    \'groupby\' => \'msOrderProduct.id\',\n    \'fastMode\' => false,\n    \'limit\' => 0,\n    \'return\' => \'data\',\n    \'decodeJSON\' => true,\n    \'nestedChunkPrefix\' => \'minishop2_\',\n);\n// Merge all properties and run!\n$pdoFetch->setConfig(array_merge($default, $scriptProperties), true);\n$rows = $pdoFetch->run();\n\n$products = array();\n$cart_count = 0;\nforeach ($rows as $product) {\n    $product[\'old_price\'] = $miniShop2->formatPrice(\n        $product[\'original_price\'] != $product[\'price\']\n            ? $product[\'original_price\']\n            : $product[\'old_price\']\n    );\n    $product[\'price\'] = $miniShop2->formatPrice($product[\'price\']);\n    $product[\'cost\'] = $miniShop2->formatPrice($product[\'cost\']);\n    $product[\'weight\'] = $miniShop2->formatWeight($product[\'weight\']);\n\n    $product[\'id\'] = (int)$product[\'id\'];\n    if (empty($product[\'name\'])) {\n        $product[\'name\'] = $product[\'pagetitle\'];\n    } else {\n        $product[\'pagetitle\'] = $product[\'name\'];\n    }\n\n    // Additional properties of product\n    if (!empty($product[\'options\']) && is_array($product[\'options\'])) {\n        foreach ($product[\'options\'] as $option => $value) {\n            $product[\'option.\' . $option] = $value;\n        }\n    }\n\n    // Add option values\n    $options = $modx->call(\'msProductData\', \'loadOptions\', array(&$modx, $product[\'id\']));\n    $products[] = array_merge($product, $options);\n\n    // Count total\n    $cart_count += $product[\'count\'];\n}\n\n$total = array(\n    \'cost\' => $miniShop2->formatPrice($order->get(\'cost\')),\n    \'cart_cost\' => $miniShop2->formatPrice($order->get(\'cart_cost\')),\n    \'delivery_cost\' => $miniShop2->formatPrice($order->get(\'delivery_cost\')),\n    \'weight\' => $miniShop2->formatWeight($order->get(\'weight\')),\n    \'cart_weight\' => $miniShop2->formatWeight($order->get(\'weight\')),\n    \'cart_count\' => $cart_count,\n);\n\n$output = $pdoFetch->getChunk($tpl, array_merge($scriptProperties, array(\n    \'order\' => $order->toArray(),\n    \'products\' => $products,\n    \'user\' => ($tmp = $order->getOne(\'User\'))\n        ? array_merge($tmp->getOne(\'Profile\')->toArray(), $tmp->toArray())\n        : array(),\n    \'address\' => ($tmp = $order->getOne(\'Address\'))\n        ? $tmp->toArray()\n        : array(),\n    \'delivery\' => ($tmp = $order->getOne(\'Delivery\'))\n        ? $tmp->toArray()\n        : array(),\n    \'payment\' => ($tmp = $order->getOne(\'Payment\'))\n        ? $tmp->toArray()\n        : array(),\n    \'total\' => $total,\n)));\n\nif ($modx->user->hasSessionContext(\'mgr\') && !empty($showLog)) {\n    $output .= \'<pre class=\"msGetOrderLog\">\' . print_r($pdoFetch->getTime(), true) . \'</pre>\';\n}\n\nif (!empty($toPlaceholder)) {\n    $modx->setPlaceholder($toPlaceholder, $output);\n} else {\n    return $output;\n}', 0, 'a:5:{s:3:\"tpl\";a:7:{s:4:\"name\";s:3:\"tpl\";s:4:\"desc\";s:12:\"ms2_prop_tpl\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:14:\"tpl.msGetOrder\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:10:\"includeTVs\";a:7:{s:4:\"name\";s:10:\"includeTVs\";s:4:\"desc\";s:19:\"ms2_prop_includeTVs\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"includeThumbs\";a:7:{s:4:\"name\";s:13:\"includeThumbs\";s:4:\"desc\";s:22:\"ms2_prop_includeThumbs\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"toPlaceholder\";a:7:{s:4:\"name\";s:13:\"toPlaceholder\";s:4:\"desc\";s:22:\"ms2_prop_toPlaceholder\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:7:\"showLog\";a:7:{s:4:\"name\";s:7:\"showLog\";s:4:\"desc\";s:16:\"ms2_prop_showLog\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}}', '', 0, 'core/components/minishop2/elements/snippets/snippet.ms_get_order.php'),
(63, 1, 0, 'msProductOptions', '', 0, 15, 0, '/** @var modX $modx */\n/** @var array $scriptProperties */\n\n$modx->setDebug(true);\n\n$tpl = $modx->getOption(\'tpl\', $scriptProperties, \'tpl.msOptions\');\nif (!empty($input) && empty($product)) {\n    $product = $input;\n}\n\n$product = !empty($product) && $product != $modx->resource->id\n    ? $modx->getObject(\'msProduct\', $product)\n    : $modx->resource;\nif (!($product instanceof msProduct)) {\n    return \"[msProductOptions] The resource with id = {$product->id} is not instance of msProduct.\";\n}\n\n$ignoreOptions = array_map(\'trim\', explode(\',\', $modx->getOption(\'ignoreOptions\', $scriptProperties, \'\')));\n$onlyOptions = array_map(\'trim\', explode(\',\', $modx->getOption(\'onlyOptions\', $scriptProperties, \'\')));\n$groups = !empty($groups)\n    ? array_map(\'trim\', explode(\',\', $groups))\n    : array();\n/** @var msProductData $data */\nif ($data = $product->getOne(\'Data\')) {\n    $optionKeys = $data->getOptionKeys();\n}\nif (empty($optionKeys)) {\n    return \'\';\n}\n$productData = $product->loadOptions();\n\n$options = array();\nforeach ($optionKeys as $key) {\n    // Filter by key\n    if (!empty($onlyOptions) && $onlyOptions[0] != \'\' && !in_array($key, $onlyOptions)) {\n        continue;\n    } elseif (in_array($key, $ignoreOptions)) {\n        continue;\n    }\n    $option = array();\n    foreach ($productData as $dataKey => $dataValue) {\n        $dataKey = explode(\'.\', $dataKey);\n        if ($dataKey[0] == $key && (count($dataKey) > 1)) {\n            $option[$dataKey[1]] = $dataValue;\n        }\n    }\n    $option[\'value\'] = $product->get($key);\n\n    // Filter by groups\n    $skip = !empty($groups) && !in_array($option[\'category\'], $groups) && !in_array($option[\'category_name\'], $groups);\n    if ($skip || empty($option[\'value\'])) {\n        continue;\n    }\n    $options[$key] = $option;\n}\n\n/** @var pdoTools $pdoTools */\n$pdoTools = $modx->getService(\'pdoTools\');\n\nreturn $pdoTools->getChunk($tpl, array(\n    \'options\' => $options,\n));', 0, 'a:5:{s:6:\"groups\";a:7:{s:4:\"name\";s:6:\"groups\";s:4:\"desc\";s:15:\"ms2_prop_groups\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"ignoreOptions\";a:7:{s:4:\"name\";s:13:\"ignoreOptions\";s:4:\"desc\";s:22:\"ms2_prop_ignoreOptions\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:11:\"onlyOptions\";a:7:{s:4:\"name\";s:11:\"onlyOptions\";s:4:\"desc\";s:20:\"ms2_prop_onlyOptions\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:7:\"product\";a:7:{s:4:\"name\";s:7:\"product\";s:4:\"desc\";s:16:\"ms2_prop_product\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:3:\"tpl\";a:7:{s:4:\"name\";s:3:\"tpl\";s:4:\"desc\";s:12:\"ms2_prop_tpl\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:20:\"tpl.msProductOptions\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}}', '', 0, 'core/components/minishop2/elements/snippets/snippet.ms_product_options.php'),
(64, 1, 0, 'TicketForm', '', 0, 16, 0, '/** @var array $scriptProperties */\n/** @var Tickets $Tickets */\n$Tickets = $modx->getService(\'tickets\', \'Tickets\', $modx->getOption(\'tickets.core_path\', null,\n        $modx->getOption(\'core_path\') . \'components/tickets/\') . \'model/tickets/\', $scriptProperties);\n$Tickets->initialize($modx->context->key, $scriptProperties);\n\nif (!$Tickets->authenticated) {\n    return $modx->lexicon(\'ticket_err_no_auth\');\n}\n\n$tplSectionRow = $modx->getOption(\'tplSectionRow\', $scriptProperties, \'tpl.Tickets.sections.row\');\n$tplFormCreate = $modx->getOption(\'tplFormCreate\', $scriptProperties, \'tpl.Tickets.form.create\');\n$tplFormUpdate = $modx->getOption(\'tplFormUpdate\', $scriptProperties, \'tpl.Tickets.form.update\');\n$tplFiles = $modx->getOption(\'tplFiles\', $scriptProperties, \'tpl.Tickets.form.files\');\n$tplFile = $Tickets->config[\'tplFile\'] = $modx->getOption(\'tplFile\', $scriptProperties, \'tpl.Tickets.form.file\', true);\n$tplImage = $Tickets->config[\'tplImage\'] = $modx->getOption(\'tplImage\', $scriptProperties, \'tpl.Tickets.form.image\',\n    true);\nif (empty($source)) {\n    $source = $Tickets->config[\'source\'] = $modx->getOption(\'tickets.source_default\', null,\n        $modx->getOption(\'default_media_source\'));\n}\n$tid = !empty($_REQUEST[\'tid\'])\n    ? (int)$_REQUEST[\'tid\']\n    : 0;\n$parent = !empty($_REQUEST[\'parent\'])\n    ? $_REQUEST[\'parent\']\n    : \'\';\n$data = array();\n\n// Update of ticket\nif (!empty($tid)) {\n    $tplWrapper = $tplFormUpdate;\n    /** @var Ticket $ticket */\n    if ($ticket = $modx->getObject(\'Ticket\', array(\'class_key\' => \'Ticket\', \'id\' => $tid))) {\n        if ($ticket->get(\'createdby\') != $modx->user->id && !$modx->hasPermission(\'edit_document\')) {\n            return $modx->lexicon(\'ticket_err_wrong_user\');\n        }\n        $charset = $modx->getOption(\'modx_charset\');\n        $allowedFields = array_map(\'trim\', explode(\',\', $scriptProperties[\'allowedFields\']));\n        $allowedFields = array_unique(array_merge($allowedFields, array(\'parent\', \'pagetitle\', \'content\')));\n\n        $fields = array_keys($modx->getFieldMeta(\'Ticket\'));\n        foreach ($allowedFields as $field) {\n            $value = in_array($field, $fields)\n                ? $ticket->get($field)\n                : $ticket->getTVValue($field);\n            if (is_string($value)) {\n                $value = html_entity_decode($value, ENT_QUOTES, $charset);\n                $value = str_replace(\n                    array(\'[^\', \'^]\', \'[\', \']\', \'{\', \'}\'),\n                    array(\'&#91;^\', \'^&#93;\', \'*(*(*(*(*(*\', \'*)*)*)*)*)*\', \'~(~(~(~(~(~\', \'~)~)~)~)~)~\'),\n                    $value\n                );\n                $value = htmlentities($value, ENT_QUOTES, $charset);\n            }\n            $data[$field] = $value;\n        }\n        $data[\'id\'] = $ticket->id;\n        $data[\'published\'] = $ticket->published;\n        if (empty($parent)) {\n            $parent = $ticket->get(\'parent\');\n        }\n    } else {\n        return $modx->lexicon(\'ticket_err_id\', array(\'id\' => $tid));\n    }\n} else {\n    $tplWrapper = $tplFormCreate;\n}\n\n// Get available sections for ticket create\n$data[\'sections\'] = \'\';\n/** @var modProcessorResponse $response */\n$response = $Tickets->runProcessor(\'web/section/getlist\', array(\n    \'parents\' => $scriptProperties[\'parents\'],\n    \'resources\' => $scriptProperties[\'resources\'],\n    \'sortby\' => !empty($scriptProperties[\'sortby\'])\n        ? $scriptProperties[\'sortby\']\n        : \'pagetitle\',\n    \'sortdir\' => !empty($scriptProperties[\'sortdir\'])\n        ? $scriptProperties[\'sortdir\']\n        : \'asc\',\n    \'depth\' => isset($scriptProperties[\'depth\'])\n        ? $scriptProperties[\'depth\']\n        : 0,\n    \'context\' => !empty($scriptProperties[\'context\'])\n        ? $scriptProperties[\'context\']\n        : $modx->context->key,\n    \'limit\' => 0,\n));\n$response = json_decode($response->getResponse(), true);\n\nif (!empty($response[\'results\'])) {\n    $Tickets->config[\'sections\'] = array();\n    foreach ($response[\'results\'] as $v) {\n        $v[\'selected\'] = $parent == $v[\'id\'] || $parent == $v[\'alias\']\n            ? \'selected\'\n            : \'\';\n        $data[\'sections\'] .= $Tickets->getChunk($tplSectionRow, $v);\n        $Tickets->config[\'sections\'][] = $v[\'id\'];\n    }\n}\n\nif (!empty($allowFiles)) {\n    $q = $modx->newQuery(\'TicketFile\');\n    $q->where(array(\'class\' => \'Ticket\'));\n    $q->andCondition(array(\'parent\' => 0, \'createdby\' => $modx->user->id), null, 1);\n    if (!empty($tid)) {\n        $q->orCondition(array(\'parent\' => $tid), null, 1);\n    }\n    $q->sortby(\'createdon\', \'ASC\');\n    $collection = $modx->getIterator(\'TicketFile\', $q);\n    $files = \'\';\n    /** @var TicketFile $item */\n    foreach ($collection as $item) {\n        if ($item->get(\'deleted\') && !$item->get(\'parent\')) {\n            $item->remove();\n        } else {\n            $item = $item->toArray();\n            $item[\'size\'] = round($item[\'size\'] / 1024, 2);\n            $item[\'new\'] = empty($item[\'parent\']);\n            $tpl = $item[\'type\'] == \'image\'\n                ? $tplImage\n                : $tplFile;\n            $files .= $Tickets->getChunk($tpl, $item);\n        }\n    }\n    $data[\'files\'] = $Tickets->getChunk($tplFiles, array(\n        \'files\' => $files,\n    ));\n    /** @var modMediaSource $source */\n    if ($source = $modx->getObject(\'sources.modMediaSource\', $source)) {\n        $properties = $source->getPropertyList();\n        $config = array(\n            \'size\' => !empty($properties[\'maxUploadSize\'])\n                ? $properties[\'maxUploadSize\']\n                : 3145728,\n            \'height\' => !empty($properties[\'maxUploadHeight\'])\n                ? $properties[\'maxUploadHeight\']\n                : 1080,\n            \'width\' => !empty($properties[\'maxUploadWidth\'])\n                ? $properties[\'maxUploadWidth\']\n                : 1920,\n            \'extensions\' => !empty($properties[\'allowedFileTypes\'])\n                ? $properties[\'allowedFileTypes\']\n                : \'jpg,jpeg,png,gif\',\n        );\n        $modx->regClientStartupScript(\'<script type=\"text/javascript\">TicketsConfig.source=\' . json_encode($config) . \';</script>\',\n            true);\n    }\n    $modx->regClientScript($Tickets->config[\'jsUrl\'] . \'web/lib/plupload/plupload.full.min.js\');\n    $modx->regClientScript($Tickets->config[\'jsUrl\'] . \'web/files.js\');\n\n    $lang = $modx->getOption(\'cultureKey\');\n    if ($lang != \'en\' && file_exists($Tickets->config[\'jsPath\'] . \'web/lib/plupload/i18n/\' . $lang . \'.js\')) {\n        $modx->regClientScript($Tickets->config[\'jsUrl\'] . \'web/lib/plupload/i18n/\' . $lang . \'.js\');\n    }\n}\n\n$output = $Tickets->getChunk($tplWrapper, $data);\n$key = md5(json_encode($Tickets->config));\n$_SESSION[\'TicketForm\'][$key] = $Tickets->config;\n$output = str_ireplace(\'</form>\',\n    \"\\n<input type=\\\"hidden\\\" name=\\\"form_key\\\" value=\\\"{$key}\\\" class=\\\"disable-sisyphus\\\" />\\n</form>\", $output);\n\nreturn $output;', 0, 'a:20:{s:13:\"tplFormCreate\";a:7:{s:4:\"name\";s:13:\"tplFormCreate\";s:4:\"desc\";s:26:\"tickets_prop_tplFormCreate\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:23:\"tpl.Tickets.form.create\";s:7:\"lexicon\";s:18:\"tickets:properties\";s:4:\"area\";s:0:\"\";}s:13:\"tplFormUpdate\";a:7:{s:4:\"name\";s:13:\"tplFormUpdate\";s:4:\"desc\";s:26:\"tickets_prop_tplFormUpdate\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:23:\"tpl.Tickets.form.update\";s:7:\"lexicon\";s:18:\"tickets:properties\";s:4:\"area\";s:0:\"\";}s:10:\"tplPreview\";a:7:{s:4:\"name\";s:10:\"tplPreview\";s:4:\"desc\";s:23